import React, { useState } from 'react'
import './Loginsignup.css'

import user_icon from '../Assets/person.png'
import email_icon from '../Assets/email.png'
import password_icon from '../Assets/password.png'

const Loginsignup = () => {
  const [action, setaction] = useState("Login")

  // Track input values
  const [name, setName] = useState("")
  const [email, setEmail] = useState("")
  const [password, setPassword] = useState("")

  // Check if all required fields are filled
  const isFormValid =
    action === "Login"
      ? email.trim() !== "" && password.trim() !== ""
      : name.trim() !== "" && email.trim() !== "" && password.trim() !== ""

  const handleSubmit = () => {
    alert(`${action} Successful!\nDetails:\n${action === "Sign Up" ? `Name: ${name}\n` : ""}Email: ${email}`)
    // You can clear fields or do more stuff here
    setName("")
    setEmail("")
    setPassword("")
  }

  return (
    <div className='container'>
      <div className="header">
        <div className="text">{action}</div>
        <div className="underline"></div>
      </div>

      <div className="inputs">
        {action === "Login" ? null : (
          <div className="input">
            <img src={user_icon} alt="User icon" />
            <input
              type="text"
              placeholder='Name'
              value={name}
              onChange={e => setName(e.target.value)}
            />
          </div>
        )}
        <div className="input">
          <img src={email_icon} alt="Email icon" />
          <input
            type="email"
            placeholder='Email Id'
            value={email}
            onChange={e => setEmail(e.target.value)}
          />
        </div>
        <div className="input">
          <img src={password_icon} alt="Password icon" />
          <input
            type="password"
            placeholder='Password'
            value={password}
            onChange={e => setPassword(e.target.value)}
          />
        </div>
      </div>

      {action === "Login" ? (
        <div className="forgot_password">Lost Password? <span>Click Here!</span></div>
      ) : null}

      {/* Show submit button only if form is valid */}
      {isFormValid && (
        <div className="submit_button" onClick={handleSubmit}>
          Submit
        </div>
      )}

      <div className="submit_container">
        <div
          className={action === "Sign Up" ? "submit gray" : "submit"}
          onClick={() => setaction("Sign Up")}
        >
          Sign Up
        </div>
        <div
          className={action === "Login" ? "submit gray" : "submit"}
          onClick={() => setaction("Login")}
        >
          Login
        </div>
      </div>
    </div>
  )
}

export default Loginsignup
