import React, { useState } from 'react';
import './Loginsignup.css';

import user_icon from '../Assets/person.png';
import email_icon from '../Assets/email.png';
import password_icon from '../Assets/password.png';

const Loginsignup = () => {
  const [action, setaction] = useState("Login");

  // Controlled input states
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  // Handle form submit (demo)
  const handleSubmit = () => {
    if (action === "Login") {
      alert(`Logging in with Email: ${email} and Password: ${password}`);
    } else {
      alert(`Signing up with Name: ${name}, Email: ${email}, Password: ${password}`);
    }
  };

  return (
    <div className='container'>
      <div className="header">
        <div className="text">{action}</div>
        <div className="underline"></div>
      </div>

      <div className="inputs">
        {action === "Sign Up" && (
          <div className="input">
            <img src={user_icon} alt="User Icon" />
            <input
              type="text"
              placeholder='Name'
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </div>
        )}
        <div className="input">
          <img src={email_icon} alt="Email Icon" />
          <input
            type="email"
            placeholder='Email Id'
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />
        </div>
        <div className="input">
          <img src={password_icon} alt="Password Icon" />
          <input
            type="password"
            placeholder='Password'
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
      </div>

      {action === "Login" && (
        <div className="forgot_password">
          Lost Password? <span style={{cursor: "pointer", color: "blue"}}>Click Here!</span>
        </div>
      )}

      <div className="submit_container">
        <button className="submit_button" onClick={handleSubmit}>
          {action}
        </button>

        {/* Show only the other action button */}
        {action === "Login" ? (
          <button className="toggle_button" onClick={() => setaction("Sign Up")}>
            Sign Up
          </button>
        ) : (
          <button className="toggle_button" onClick={() => setaction("Login")}>
            Login
          </button>
        )}
      </div>
    </div>
  );
}

export default Loginsignup;
